import React, {Component} from "react";
import { getSomeBeer } from "../../actions";
import WithBtdService from "../../hoc";
import {connect} from "react-redux";
import BeerDetails from "../beer-details";
import Spinner from "../spinner";

class RandBeerPage extends Component {
    // componentDidMount() {
    //
    // }

    render(){
        const idRand = Math.floor(Math.random()*3);
        this.props.getSomeBeer(idRand);
        const {currentBeer} = this.props;
        if(currentBeer.id !== idRand){
            return(
                <Spinner/>
            )
        }
        return(
            <BeerDetails currentBeer={currentBeer}/>
        )
    }
}
const mapStateToProps = ({ currentBeer}) => {
    return{
        currentBeer
    }
}
const mapDispatchToProps = (dispatch) => {
    return{
        getSomeBeer: (id) => {dispatch(getSomeBeer(id))}
    }
}

export default WithBtdService()(connect(mapStateToProps, mapDispatchToProps)(RandBeerPage));
