import React, { Component } from "react";
import './beer-page.css';
import Spinner from "../spinner";
import { connect } from 'react-redux';
import Row from "../row";
import {fetchAllBeer, getSomeBeer} from "../../actions";
import WithBtdService from "../../hoc";
import { renderAllBeer } from "./beer-list";
import ErrorIndicator from "../error-indicator";

class BeerPage extends Component {
    componentDidMount() {
        this.props.fetchAllBeer();
    }
    beerDetails(id) {
        const beer = this.props.getSomeBeer(id);
        return(
            <div className='item-details jumbotron d-flex rounded'>
                <img className='item-image'
                     src={image}
                     alt='item'/>
                <div>
                    <h4 className='item-name'>{name}</h4>
                    <ul className='list-group list-group-flush'>
                        {
                            //{children}
                            React.Children.map(children, (child) => {
                                return(React.cloneElement(child, { item }));
                            })
                        }
                    </ul>
                </div>
            </div>
        )
    }
    render(){
        const {allBeer, loading, error} = this.props;
        const beerList = renderAllBeer(allBeer);
        if(error) {return(<ErrorIndicator/>)}
        if(loading){return <Spinner/>}

        return(
            <Row left={beerList} right={<Spinner/>}/>
        )

    }

}
const mapStateToProps = ({beer, loading, error, currentBeer}) => {
    return{
        allBeer: beer,
        loading,
        error,
        currentBeer
    }
}
const mapDispatchToProps = (dispatch, {btdService}) => {
    return{
        fetchAllBeer: fetchAllBeer(dispatch, btdService),
        getSomeBeer: getSomeBeer
    }
}

export default WithBtdService()(connect(mapStateToProps, mapDispatchToProps)(BeerPage));
