import React, { Component } from "react";
import './beer-page.css';
import Spinner from "../spinner";
import { connect } from 'react-redux';
import Row from "../row";
import {fetchAllBeer} from "../../actions";
import WithBtdService from "../../hoc";

class BeerPage extends Component {
    componentDidMount() {
        this.props.fetchAllBeer();
    }
    renderBeer(allBeer) {
        return allBeer.map((beer) => {
            const {id, title} = beer;
            return(
                <li className='list-group-item'
                    key={id}
                    >
                    <span className='term'>{title}</span>
                </li>
            );
        })
    }
    render(){
        const {allBeer, loading, error} = this.props;
        const beerList = this.renderBeer(allBeer);
        return(
            <Row left={
                <ul className='item-list list-group'>
                    {beerList}
                </ul>
            } right={<Spinner/>}/>
        )

    }

}
const mapStateToProps = ({beer, loading, error}) => {
    return{
        allBeer: beer,
        loading,
        error
    }
}
const mapDispatchToProps = (dispatch, {btdService}) => {
    return{
        fetchAllBeer:fetchAllBeer(dispatch, btdService)
    }
}

export default WithBtdService()(connect(mapStateToProps, mapDispatchToProps)(BeerPage));
