import React, { Component } from "react";
import './beer-page.css';
import Spinner from "../spinner";
import { connect } from 'react-redux';
import Row from "../row";
import {fetchAllBeer, getSomeBeer} from "../../actions";
import WithBtdService from "../../hoc";
//import BeerList from "../beer-list";
import ErrorIndicator from "../error-indicator";

class BeerPage extends Component {
    componentDidMount() {
        this.props.fetchAllBeer();
    }
    beerDetails() {
        const {currentBeer} = this.props;
        const { title, alc} = currentBeer;
        return(
            <div className='item-details jumbotron d-flex rounded'>
                <img className='item-image'
                     src={'https://avatars.mds.yandex.net/get-pdb/2729594/0a261944-fd06-48d1-90bf-e39a8d70a564/s1200'}
                     alt='item'/>
                <div>
                    <h4 className='item-name'>{title}</h4>
                    <ul className='list-group list-group-flush'>
                        <li className='list-group-item'>
                            <span className='term'>alcohol</span>
                            <span>{alc}</span>
                        </li>
                    </ul>
                </div>
            </div>
        )
    }

    render(){
        const {allBeer, loading, error, getSomeBeer} = this.props;
        //const beerList = renderAllBeer(allBeer);
        if(error) {return(<ErrorIndicator/>)}
        if(loading){return <Spinner/>}
        //getSomeBeer(2);
        const bb = this.beerDetails();
        return(
            <Row left={<BeerList allBeer={allBeer} getSomeBeer={getSomeBeer}/> } right={bb}/>
        )

    }

}
const BeerList = ({allBeer, getSomeBeer}) => {
    return (
        <ul className='item-list list-group'>
            {
                allBeer.map((beer) => {
                    const {id, title} = beer;
                    return (
                        <li className='list-group-item'
                            key={id}
                            onClick={() => getSomeBeer(id)}
                        >
                            <span className='term'>{title}</span>
                        </li>
                    );
                })
            }
        </ul>
    )
}
const mapStateToProps = ({beer, loading, error, currentBeer}) => {
    return{
        allBeer: beer,
        loading,
        error,
        currentBeer
    }
}
const mapDispatchToProps = (dispatch, {btdService}) => {
    return{
        fetchAllBeer: fetchAllBeer(dispatch, btdService),
        getSomeBeer: (id) => {dispatch(getSomeBeer(id))}
    }
}

export default WithBtdService()(connect(mapStateToProps, mapDispatchToProps)(BeerPage));
