const initialState = {
    beer: [],
    loading: true,
    error: null,
    currentBeer: {
        id: 1,
        title: 'Жигулівське',
        alc: 4.2,
        price: 17,
        description: 'ЖИВЕ непастеризоване "Жигулівське" пиво з Умані порадує Вас незмінним та незабутнім хмільним смаком , який завжди до вподоби як і тим , хто його давно вживає ,так і тим , хто куштує його вперше!'
    }
};

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'FETCH_ALL_BEER_REQUEST':
            return{
                beer: [],
                loading: true,
                error: null
            };
        case 'FETCH_ALL_BEER_SUCCESS':
            return{
                beer: action.payload,
                loading: false,
                error: null
            };
        case'FETCH_BEER_FAILURE':
            return{
                beer: [],
                loading: true,
                error: action.payload
            };
        case 'SOME_BEER_REQUEST':
            const beerId = action.payload;
            const curBeer = state.beer.find((el) => el.id === beerId);
            return{
                ...state,
                currentBeer: curBeer
            }
        default: return state;
    }
}
export default reducer;
