import React from "react";
import './beer-details.css';

const BeerDetails= ({currentBeer}) => {
    if (currentBeer === []){
        return(
            <div>
                Select Pivo!
            </div>
        )
    }
    const { title, alc} = currentBeer;
    return(
        <div className='item-details jumbotron d-flex rounded'>
            <img className='item-image'
                 src={'https://avatars.mds.yandex.net/get-pdb/2729594/0a261944-fd06-48d1-90bf-e39a8d70a564/s1200'}
                 alt='item'/>
            <div>
                <h4 className='item-name'>{title}</h4>
                <ul className='list-group list-group-flush'>
                    <li className='list-group-item'>
                        <span className='term'>alcohol</span>
                        <span>{alc}</span>
                    </li>
                </ul>
            </div>
        </div>
    )
}

export default BeerDetails;
