import React from "react";
import './app.css';
import Header from "../header";
import Spinner from "../spinner";
import BeerPage from "../beer-page";
import { Route, Switch } from 'react-router-dom'


const App = () => {
    return(
        <div className='app'>
            <Header/>
            <Switch>
                <Route/>
            </Switch>
            <BeerPage/>
        </div>

    )
}

export default App;
